INSERT INTO `wp_options` VALUES (124,'_transient_dash_4077549d03da2e451c8b5f002294ff51','<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2014/11/wordpress-4-0-1/\'>WordPress 4.0.1 Security Release</a> <span class=\"rss-date\">November 20, 2014</span><div class=\"rssSummary\">WordPress 4.0.1 is now available. This is a critical security release for all previous versions and we strongly encourage you to update your sites immediately. Sites that support automatic background updates will be updated to WordPress 4.0.1 within the next few hours. If you are still on WordPress 3.9.2, 3.8.4, or 3.7.4, you will be [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/what-would-it-take-for-wordpress-to-lose-its-dominance\'>WPTavern: What Would it Take For WordPress to Lose its Dominance?</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/minnow-a-free-minimalist-blogging-theme-from-automattic\'>WPTavern: Minnow: A Free Minimalist Blogging Theme from Automattic</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/roots-starter-theme-for-wordpress-will-become-framework-agnostic-in-2015\'>WPTavern: Roots Starter Theme for WordPress Will Become Framework Agnostic in 2015</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/wysija-newsletters/\' class=\'dashboard-news-plugin-link\'>MailPoet Newsletters</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=wysija-newsletters&amp;_wpnonce=fffdacec61&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'MailPoet Newsletters\'>Install</a>)</span></li></ul></div>','no');
